import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import TaskList from './components/TaskList';
import UserManagement from './components/UserManagement';
import TaskModal from './components/TaskModal';
import './styles/App.css';
import {setIsLoggedIn} from './Main';

function App({ role }) {
  const [tasks, setTasks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/tasks', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(response.data || []);
    } catch (error) {
      console.error('Error fetching tasks', error);
    }
  };

  const handleAddTask = () => {
    setCurrentTask(null);
    setShowModal(true);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowModal(true);
  };

  const handleSaveTask = async (task) => {
    try {
      if (currentTask) {
        await axios.put(`http://localhost:8080/api/tasks/${task.id}`, task, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setTasks(tasks.map(t => t.id === task.id ? task : t));
      } else {
        const response = await axios.post('http://localhost:8080/api/tasks', task, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setTasks([...tasks, response.data]);
      }
      setShowModal(false);
    } catch (error) {
      console.error('Error saving task', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(tasks.filter(t => t.id !== id));
    } catch (error) {
      console.error('Error deleting task', error);
    }
  };

  return (
    <div className="app">
      <Header />
      <div className="main">
        <Sidebar role={role} />
        <div className="content">
          <Routes>
            <Route path="/dashboard" element={<Dashboard tasks={tasks} onAddTask={handleAddTask} />} />
            <Route path="/tasks" element={<TaskList tasks={tasks} onEditTask={handleEditTask} onDeleteTask={handleDeleteTask} />} />
            {role === 'admin' && <Route path="/user-management" element={<UserManagement onTaskAssigned={fetchTasks} />} />}
            <Route path="/settings" element={<div>Settings Component</div>} />
            <Route path="/" element={<Navigate to="/dashboard" />} />
          </Routes>
        </div>
      </div>
      {showModal && (
        <TaskModal
          task={currentTask}
          onSave={handleSaveTask}
          onClose={() => setShowModal(false)}
        />
      )}
      <footer>
        <button onClick={() => setIsLoggedIn(false)}>Logout</button>
      </footer>
    </div>
  );
}

export default App;
